(function($){$.fn.Carousel=function(options){var defaults={fullScreen:true,index:1,flag:true,speed:2000,isNav:true,isPage:true,};var op=$.extend(defaults,options);var animate,timer,stop,nav,fun_animate,plusminus,screens,addDom,init=null;var carousel=$('#carousel');var list=$("#wrapper");var li=list.find('li');var prev=$("#prev");var next=$("#next");var liLength=li.length;var tagLiLeng=liLength+1;var animated=false;var liWidth=null;screens=function(){if(op.fullScreen){$(window).resize(function(){location.reload()});liWidth=$(window).width();carousel.width(liWidth);list.width(liWidth*tagLiLeng);list.children().width(liWidth).find('img').width(liWidth)}else{liWidth=li.eq(0).width();list.width(liWidth*tagLiLeng)}list.css('left',-liWidth);};animate=function(offset){newLeft=list.position().left+offset;var maxWidth=liWidth*(liLength-1);if(newLeft< -maxWidth-liWidth){list.css('left','0px');fun_animate(-liWidth)}else if(newLeft>0){list.css('left',-maxWidth-liWidth);fun_animate(-maxWidth)}else{fun_animate(newLeft)}};addDom=function(){list.append(li.eq(0).clone());list.prepend(li.eq(liLength-1).clone())};fun_animate=function(lef){if(animated){return}animated=true;list.animate({left:lef},function(){$('#pagination span').eq(op.index-1).addClass('on').siblings().removeClass('on');animated=false})};timer=function(){if(op.flag){auto=setInterval(function(){plusminus(-liWidth)},op.speed)}};stop=function(){clearInterval(auto)};nav=function(){if(op.isNav){var spans='<span></span>';var arr = new Array(liLength + 1);spans=arr.join(spans);list.after('<div id="pagination">'+spans+'</div>');$('#pagination span').eq(0).addClass('on');$('#pagination span').click(function(){navClick($(this))})}};navClick=function(e){var _index=$('#pagination span').index(e)+1;var offset=liWidth*(op.index-_index);animate(offset);op.index=_index};carousel.mouseover(function(){stop();if(op.isPage){prev.show();next.show()}});carousel.mouseout(function(){timer();prev.hide();next.hide()});plusminus=function(w){if(!animated){if(w>0){op.index-=1}else{op.index+=1}if(op.index<1){op.index=liLength}if(op.index>liLength){op.index=1}animate(w)}};prev.click(function(){plusminus(liWidth)});next.click(function(){plusminus(-liWidth)});init=function(){screens();addDom();timer();nav()};init();return this}})(jQuery);